mapper.select:
select id, provider_id as providerId, nickname, gender, age_range as ageRange, created_at as createdAt, updated_at as updatedAt
from members
where provider_id = #{providerId};

select id, provider_id as providerId, nickname, gender, age_range as ageRange, created_at as createdAt, updated_at as updatedAt
from members
where id = #{id};

mapper.update:
update members
set nickname = #{nickname}
where id = #{id};
